package br.com.restaurante.faces;import java.util.ArrayList;import java.util.List;import javax.annotation.PostConstruct;import javax.ejb.EJB;import javax.faces.bean.ManagedBean;import javax.faces.bean.ViewScoped;import javax.faces.model.SelectItem;import br.com.restaurante.business.ComboBS;import br.com.restaurante.business.CrudBS;import br.com.restaurante.business.OrigemBS;import br.com.restaurante.business.UsuarioBS;import br.com.restaurante.model.CidadeModel;import br.com.restaurante.model.EstadoModel;import br.com.restaurante.model.OrigemModel;import br.com.topsys.util.TSUtil;@ViewScoped@SuppressWarnings("serial")@ManagedBean(name = "origemFaces")public final class OrigemFaces extends CrudFaces<OrigemModel> {	@EJB	private OrigemBS origemBS;	@EJB	private UsuarioBS usuarioBS;	@EJB	private ComboBS comboBS;	private List<SelectItem> comboEstados;	private List<SelectItem> comboCidades;	@Override	@PostConstruct	protected void clearFields() {		this.crudModel = new OrigemModel();		this.crudModel.setCidadeModel(new CidadeModel());		this.crudModel.getCidadeModel().setEstadoModel(new EstadoModel());		this.crudModel.setFlagAtivo(true);		this.crudPesquisaModel = new OrigemModel();		this.comboEstados = super.initCombo(this.comboBS.pesquisarEstados(), "id", "sigla");	}	public void atualizarCidades() {		if (TSUtil.isEmpty(this.crudModel.getCidadeModel().getEstadoModel().getId())) {			this.comboCidades = new ArrayList<SelectItem>();		} else {			this.comboCidades = super.initCombo(this.comboBS.pesquisarCidades(this.crudModel.getCidadeModel().getEstadoModel()), "id", "descricao");		}	}	@Override	protected void posDetail() {		this.atualizarCidades();	}	@Override	protected CrudBS<OrigemModel> getCrudBS() {		return origemBS;	}	public List<SelectItem> getComboEstados() {		return comboEstados;	}	public void setComboEstados(List<SelectItem> comboEstados) {		this.comboEstados = comboEstados;	}	public List<SelectItem> getComboCidades() {		return comboCidades;	}	public void setComboCidades(List<SelectItem> comboCidades) {		this.comboCidades = comboCidades;	}}