package br.com.restaurante.faces;import javax.annotation.PostConstruct;import javax.ejb.EJB;import javax.faces.bean.ManagedBean;import javax.faces.bean.ViewScoped;import br.com.restaurante.business.CrudBS;import br.com.restaurante.business.UnidadeMedidaBS;import br.com.restaurante.model.UnidadeMedidaModel;@ViewScoped@SuppressWarnings("serial")@ManagedBean(name = "unidadeMedidaFaces")public final class UnidadeMedidaFaces extends CrudFaces<UnidadeMedidaModel> {	@EJB	private UnidadeMedidaBS unidadeMedidaBS;	@Override	@PostConstruct	protected void clearFields() {		super.clearFields();		this.crudModel = new UnidadeMedidaModel();		this.crudPesquisaModel = new UnidadeMedidaModel();	}	@Override	protected CrudBS<UnidadeMedidaModel> getCrudBS() {		return this.unidadeMedidaBS;	}}