package br.com.restaurante.model;import java.util.List;@SuppressWarnings("serial")public class AtividadeModel extends BaseModel implements CrudModel<AtividadeModel> {	private String descricao;	private String descricaoCompleta;	private String codigo;		private List<AtividadeModel> historico;	public AtividadeModel(String descricao) {		super();		this.descricao = descricao;	}	public AtividadeModel() {		super();	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}	public String getCodigo() {		return codigo;	}	public void setCodigo(String codigo) {		this.codigo = codigo;	}	public List<AtividadeModel> getHistorico() {		return historico;	}	public void setHistorico(List<AtividadeModel> historico) {		this.historico = historico;	}	public String getDescricaoCompleta() {		return descricaoCompleta;	}	public void setDescricaoCompleta(String descricaoCompleta) {		this.descricaoCompleta = descricaoCompleta;	}	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((descricao == null) ? 0 : descricao.toUpperCase().hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj)			return true;		if (getClass() != obj.getClass())			return false;		AtividadeModel other = (AtividadeModel) obj;		if (descricao == null) {			if (other.descricao != null)				return false;		} else if (!descricao.equalsIgnoreCase(other.descricao))			return false;		return true;	}}