package br.com.restaurante.model;import java.util.List;@SuppressWarnings("serial")public class CargoModel extends BaseModel implements CrudModel<CargoModel> {	private String descricao;	private String codigo;		private List<CargoModel> historico;	public CargoModel(String codigo) {		super();		this.codigo = codigo;	}	public CargoModel() {		super();	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}	public String getCodigo() {		return codigo;	}	public void setCodigo(String codigo) {		this.codigo = codigo;	}	public List<CargoModel> getHistorico() {		return historico;	}	public void setHistorico(List<CargoModel> historico) {		this.historico = historico;	}	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((codigo == null) ? 0 : codigo.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj)			return true;		if (getClass() != obj.getClass())			return false;		CargoModel other = (CargoModel) obj;		if (codigo == null) {			if (other.codigo != null)				return false;		} else if (!codigo.equals(other.codigo))			return false;		return true;	}}