package br.com.restaurante.model;import java.util.Date;import java.util.List;import br.com.restaurante.util.Constantes;import br.com.restaurante.util.Utilitario;import br.com.topsys.util.TSUtil;@SuppressWarnings("serial")public class FuncionarioModel extends BaseModel implements CrudModel<FuncionarioModel> {	private String nome;		private String apelido;		private String email;		private String cpf;		private String rg;		private String foto;		private String telefone;		private String telefoneComercial;		private String celular;		private String matricula;		private String observacao;		private Date dataNascimento;		private List<FuncionarioModel> historico;		private Integer sexo;		private String endereco;		private String numero;		private String complemento;		private BairroModel bairroModel;		private CidadeModel cidadeModel;		private EstadoModel estadoModel;		private OrigemModel origemModel;		private String cep;		private Long idExterno;		private CargoModel cargoModel;		private AtividadeModel atividadeModel;		private Integer qtdDependentes;		private List<AtendimentoModel> atendimentos;		public String getNome() {		return nome;	}	public void setNome(String nome) {		this.nome = nome;	}	public String getApelido() {		return apelido;	}	public void setApelido(String apelido) {		this.apelido = apelido;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public String getCpf() {		return cpf;	}	public void setCpf(String cpf) {		this.cpf = cpf;	}	public String getRg() {		return rg;	}	public void setRg(String rg) {		this.rg = rg;	}	public String getTelefone() {		return telefone;	}	public void setTelefone(String telefone) {		this.telefone = telefone;	}	public String getMatricula() {		return matricula;	}	public void setMatricula(String matricula) {		this.matricula = matricula;	}	public String getObservacao() {		return observacao;	}	public void setObservacao(String observacao) {		this.observacao = observacao;	}	public Date getDataNascimento() {		return dataNascimento;	}	public void setDataNascimento(Date dataNascimento) {		this.dataNascimento = dataNascimento;	}	public List<FuncionarioModel> getHistorico() {		return historico;	}	public void setHistorico(List<FuncionarioModel> historico) {		this.historico = historico;	}	public Integer getSexo() {		return sexo;	}	public void setSexo(Integer sexo) {		this.sexo = sexo;	}	public String getEndereco() {		return endereco;	}	public void setEndereco(String endereco) {		this.endereco = endereco;	}	public String getNumero() {		return numero;	}	public void setNumero(String numero) {		this.numero = numero;	}	public String getComplemento() {		return complemento;	}	public void setComplemento(String complemento) {		this.complemento = complemento;	}	public BairroModel getBairroModel() {		return bairroModel;	}	public void setBairroModel(BairroModel bairroModel) {		this.bairroModel = bairroModel;	}	public CidadeModel getCidadeModel() {		return cidadeModel;	}	public void setCidadeModel(CidadeModel cidadeModel) {		this.cidadeModel = cidadeModel;	}	public EstadoModel getEstadoModel() {		return estadoModel;	}	public void setEstadoModel(EstadoModel estadoModel) {		this.estadoModel = estadoModel;	}	public String getCep() {		return cep;	}	public void setCep(String cep) {		this.cep = cep;	}	public Long getIdExterno() {		return idExterno;	}	public void setIdExterno(Long idExterno) {		this.idExterno = idExterno;	}	public CargoModel getCargoModel() {		return cargoModel;	}	public void setCargoModel(CargoModel cargoModel) {		this.cargoModel = cargoModel;	}	public String getCelular() {		return celular;	}	public void setCelular(String celular) {		this.celular = celular;	}		public String getTelefoneComercial() {		return telefoneComercial;	}	public void setTelefoneComercial(String telefoneComercial) {		this.telefoneComercial = telefoneComercial;	}		public AtividadeModel getAtividadeModel() {		return atividadeModel;	}	public void setAtividadeModel(AtividadeModel atividadeModel) {		this.atividadeModel = atividadeModel;	}	public Integer getQtdDependentes() {		return qtdDependentes;	}	public void setQtdDependentes(Integer qtdDependentes) {		this.qtdDependentes = qtdDependentes;	}	public OrigemModel getOrigemModel() {		return origemModel;	}	public void setOrigemModel(OrigemModel origemModel) {		this.origemModel = origemModel;	}	public List<AtendimentoModel> getAtendimentos() {		return atendimentos;	}	public void setAtendimentos(List<AtendimentoModel> atendimentos) {		this.atendimentos = atendimentos;	}	public String getFoto() {		return foto;	}	public void setFoto(String foto) {		this.foto = foto;	}		public String getFotoView(){		return Constantes.PASTA_DOWNLOAD_ARQUIVO + Constantes.PASTA_FOTOS + this.foto;	}	public String getDescricaoSexo() {		return TSUtil.isEmpty(this.sexo) ? null : this.sexo == 1 ? "MASCULINO" : "FEMININO";	}	public String getDescricaoIdade() {		return TSUtil.isEmpty(this.dataNascimento) ? null : Utilitario.calcularIdade(this.dataNascimento); 	}}