package br.com.restaurante.model;import java.util.List;@SuppressWarnings("serial")public class UnidadeMedidaModel extends BaseModel implements CrudModel<UnidadeMedidaModel>{	private Long idExterno;		private String descricao;		private String descricaoDetalhada;		private List<UnidadeMedidaModel> historico;		public UnidadeMedidaModel(Long id) {		super();		this.id = id;	}	public UnidadeMedidaModel() {		super();	}	public Long getIdExterno() {		return idExterno;	}	public void setIdExterno(Long idExterno) {		this.idExterno = idExterno;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}	public String getDescricaoDetalhada() {		return descricaoDetalhada;	}	public void setDescricaoDetalhada(String descricaoDetalhada) {		this.descricaoDetalhada = descricaoDetalhada;	}	public List<UnidadeMedidaModel> getHistorico() {		return historico;	}	public void setHistorico(List<UnidadeMedidaModel> historico) {		this.historico = historico;	}}